const { cadencia_lembretes } = $('Info').item.json;
const agora = new Date();
const inicio_evento = new Date($json.start.dateTime);

const horas_ate_evento = (inicio_evento.getTime() - agora.getTime()) / (1000 * 60 * 60);

for (const lembrete of cadencia_lembretes) {
  const chave_lembrete = `lembrete_enviado_${lembrete}`;
  if ($json.extendedProperties?.private?.[chave_lembrete] === 'true') {
    // Pula se lembrete já foi enviado
    continue;
  }

  // Não disparamos o lembrete se já passou mais de 1h da hora que ele deveria disparar.
  const limite_inferior = lembrete - 1;
  const limite_superior = lembrete;

  if (horas_ate_evento >= limite_inferior && horas_ate_evento <= limite_superior) {
    $input.item.json.lembrete = lembrete;
    return $input.item;
  }
}

return {};

